function shortCodePre(a, b, c) {
    a = a.split("$");
    for (var d = /[^{\}]+(?=})/g, e = 0; e < a.length; e++) {
        var f = a[e].split("=");
        if (f[0].trim() == b) return null != (c = f[1]).match(d) && String(c.match(d)).trim()
    }
    return !1
}

function msgError() {
    return '<span class="error-msg"><b>Error:</b>&nbsp;No Results Found</span>'
}

function beforeLoader() {
    return '<div class="loader"><div class="loader-ellipsis"><div></div><div></div><div></div><div></div></div></div>'
}

function getFeedUrl(a, b, c, d) {
    switch (c) {
        case "recent":
            d = "/feeds/posts/default?alt=json&max-results=" + b;
            break;
        case "comments":
            d = "list1" == a ? "/feeds/comments/default?alt=json&max-results=" + b : "/feeds/posts/default/-/" + c + "?alt=json&max-results=" + b;
            break;
        default:
            d = "/feeds/posts/default/-/" + c + "?alt=json&max-results=" + b
    }
    return d
}

function getPostLink(a, b) {
    for (var c = 0; c < a[b].link.length; c++)
        if ("alternate" == a[b].link[c].rel) {
            var d = a[b].link[c].href;
            break
        } return d
}

function getPostTitle(a, b, c) {
    return a[b].title.$t ? a[b].title.$t : exportify.noTitle
}

function getFirstImage(a, b) {
    var c = $("<div>").html(a).find("img:first").attr("src"),
        d = c.lastIndexOf("/") || 0,
        e = c.lastIndexOf("/", d - 1) || 0,
        f = c.substring(0, e);
    e = c.substring(e, d);
    c = c.substring(d);
    return (e.match(/\/s[0-9]+/g) || e.match(/\/w[0-9]+/g) || "/d" == e) && (e = "/w72-h72-p-k-no-nu"), f + e + c
}

