    d = a[b].content.$t;
    return c = a[b].media$thumbnail ? a[b].media$thumbnail.url : "https://resources.blogblog.com/img/blank.gif", -1 < d.indexOf(d.match(/<iframe(?:.+)?src=(?:.+)?(?:www.youtube.com)/g)) ? -1 < d.indexOf("<img") ? d.indexOf(d.match(/<iframe(?:.+)?src=(?:.+)?(?:www.youtube.com)/g)) < d.indexOf("<img") ? c.replace("/default.", "/maxresdefault.") : getFirstImage(d) : c.replace("/default.", "/maxresdefault.") : -1 < d.indexOf("<img") ? getFirstImage(d) : "https://resources.blogblog.com/img/blank.gif"
}

function getPostAuthor(a, b, c) {
    return '<span class="author-name">' + a[b].author[0].name.$t + "</span>"
}

function getPostDate(a, b, c) {
    a = a[b].published.$t;
    b = a.substring(0, 4);
    c = a.substring(5, 7);
    var d = a.substring(8, 10);
    return '<time class="published" datetime="' + a + '">' + dateFormat.replace("{m}", monthNames[parseInt(c, 10) - 1]).replace("{d}", d).replace("{y}", b) + "</time>"
}

