    return c = "" != exportify.authorLabel ? '<span class="by">' + exportify.authorLabel + "</span>" : "", d = "" != exportify.dateLabel && 1 == exportify.postAuthor ? '<span class="on">' + exportify.dateLabel + "</span>" : "", [1 == exportify.postAuthor && 1 == exportify.postDate ? '<div class="entry-meta"><span class="entry-author">' + c + a + '</span><span class="entry-time">' + d + b + "</span></div>" : 1 == exportify.postAuthor ? '<div class="entry-meta"><span class="entry-author">' + c + a + "</span></div>" : 1 == exportify.postDate ?
        '<div class="entry-meta"><span class="entry-time">' + d + b + "</span></div>" : "", 1 == exportify.postDate ? '<div class="entry-meta"><span class="entry-time">' + b + "</span></div>" : ""
    ]
}

