function getAjax(a, b, c, d) {
    switch (b) {
        case "msimple":
        case "featured":
        case "list1":
        case "related":
            0 == d && (d = "geterror404"), c = getFeedUrl(b, c, d), $.ajax({
                url: c,
                type: "GET",
                dataType: "json",
                cache: !0,
                beforeSend: function(e) {
                    switch (b) {
                        case "featured":
                            a.html(beforeLoader()).parent().addClass("show-post");
                            break;
                        case "list1":
                            a.html(beforeLoader());
                            break;
                        case "related":
                            a.html(beforeLoader()).parent().addClass("show-post")
                    }
                },
                success: function(e) {
                    var f = "";
                    switch (b) {
                        case "msimple":
                            f = '<div class="ul mega-items">';
                            break;
                        case "featured":
                            f = '<div class="featured-items">';
                            break;
                        case "list1":
                            f = "comments" != d ? '<div class="list1-items sidebar-posts">' : '<div class="cmm1-items">';
                            break;
                        case "related":
                            f = '<div class="related-posts">'
                    }
                    e = e.feed.entry;
                    if (null != e)
                        for (var g = 0; g < e.length; g++) {
                            var h = getPostLink(e, g),
                                k = getPostTitle(e, g),
                                l = getPostImage(e, g),
                                r = getPostMeta(getPostAuthor(e, g), getPostDate(e, g)),
                                q = getPostTag(e, g),
                                p = getPostImageType(l, g),
                                m = "";
                            switch (b) {
                                case "msimple":
                                    m += '<div class="mega-item post"><div class="mega-content"><a title="' +
                                        k + '" class="entry-image-wrap ' + p + '" href="' + h + '"><span class="entry-thumb" data-image="' + l + '"></span>' + q + '</a><h2 class="entry-title"><a href="' + h + '">' + k + "</a></h2></div></div>";
                                    break;
                                case "featured":
                                    switch (g) {
                                        case 0:
                                            m += '<div class="featured-item item-' + g + '"><a class="entry-inner wrap-all-link" href="' + h + '"><span class="entry-image-wrap ' + p + ' c-ply before-mask"><span class="entry-thumb" data-image="' + l + '"></span>' + q + '</span><div class="entry-header entry-info"><h2 class="entry-title">' + k + "</h2>" +
                                                r[0] + '</div></a></div><div class="featured-scroll">';
                                            break;
                                        default:
                                            m += '<div class="featured-item item-' + g + '"><a class="entry-inner wrap-all-link" href="' + h + '"><span class="entry-image-wrap ' + p + ' before-mask"><span class="entry-thumb" data-image="' + l + '"></span>' + q + '</span><div class="entry-header entry-info"><h2 class="entry-title">' + k + "</h2>" + r[1] + "</div></a></div>"
                                    }
                                    break;
                                case "list1":
                                    switch (d) {
                                        case "comments":
                                            m += getPostComments(e, g, h);
                                            break;
                                        default:
                                            switch (g) {
                                                case 0:
                                                    m += '<div class="list1-item post big-post item-' +
                                                        g + '"><a class="entry-inner wrap-all-link" href="' + h + '"><span class="entry-image-wrap before-mask ' + p + '"><span class="entry-thumb" data-image="' + l + '"></span>' + q + '</span><div class="entry-header entry-info"><h2 class="entry-title">' + k + "</h2>" + r[1] + "</div></a></div>";
                                                    break;
                                                default:
                                                    m += '<div class="list1-item post item-' + g + '"><a title="' + k + '" class="entry-image-wrap ' + p + '" href="' + h + '"><span class="entry-thumb" data-image="' + l + '"/></a><div class="entry-header"><h2 class="entry-title"><a href="' + h + '">' +
                                                        k + "</a></h2></div></div>"
                                            }
                                    }
                                    break;
                                case "related":
                                    m += '<div class="related-item post item-' + g + '"><a title="' + k + '" class="entry-image-wrap ' + p + '" href="' + h + '"><span class="entry-thumb" data-image="' + l + '"></span>' + q + '</a><div class="entry-header"><h2 class="entry-title"><a href="' + h + '">' + k + "</a></h2></div></div>"
                            }
                            f += m
                        } else switch (b) {
                            case "msimple":
                                f = '<div class="ul mega-items no-items">' + msgError() + "</div>";
                                break;
                            default:
                                f = msgError()
                        }
                    switch (b) {
                        case "msimple":
                            a.append(f + "</div>").addClass("msimple");
                            a.find("a:first").attr("href",
                                function(t, n) {
                                    switch (d) {
                                        case "recent":
                                            n = n.replace(n, "/search/max-results=6");
                                            break;
                                        default:
                                            n = n.replace(n, "/search/label/" + d)
                                    }
                                    return n
                                });
                            break;
                        case "featured":
                            a.html(f + "</div></div>");
                            break;
                        default:
                            a.html(f + "</div>")
                    }
                    a.find("span.entry-thumb").lazyload()
                },
                error: function() {
                    switch (b) {
                        case "msimple":
                            a.append('<div class="ul mega-items no-items">' + msgError() + "</div>");
                            break;
                        default:
                            a.html(msgError())
                    }
                }
            })
    }
}

function ajaxMega(a, b, c, d, e) {
    if (e.match("getmega")) {
        if ("msimple" == b) return getAjax(a, b, c, d);
        a.append('<div class="ul mega-items no-items">' + msgError() + "</div>")
    }
}

function ajaxFeatured(a, b, c, d, e) {
    if (e.match("getfeatured")) {
        if ("featured" == b) return getAjax(a, b, c, d);
        a.html(beforeLoader()).parent().addClass("show-post");
        setTimeout(function() {
            a.html(msgError())
        }, 500)
    }
}

function ajaxWidget(a, b, c, d, e) {
    if (e.match("getwidget")) {
        if ("list1" == b) return getAjax(a, b, c, d);
        a.html(msgError())
    }
}

function ajaxRelated(a, b, c, d, e) {
    if (e.match("getrelated")) return getAjax(a, b, c, d)
}

function beautiAvatar(a) {
    $(a).attr("src", function(b, c) {
        return c.replace("//resources.blogblog.com/img/blank.gif", "//4.bp.blogspot.com/-oSjP8F09qxo/Wy1J9dp7b0I/AAAAAAAACF0/ggcRfLCFQ9s2SSaeL9BFSE2wyTYzQaTyQCK4BGAYYCw/s35-r/avatar.jpg")
    })
}

function commentsSystemTitle(a) {
    $(a).text($(a).data("title"))
}
