$(".post-body blockquote").each(function() {
    var a = $(this),
        b = a.text().toLowerCase().trim(),
        c = a.html();
    if (b.match("{alertsuccess}")) {
        var d = c.replace("{alertSuccess}", "");
        a.replaceWith('<div class="alert-message alert-success">' + d + "</div>")
    }
    b.match("{alertinfo}") && (d = c.replace("{alertInfo}", ""), a.replaceWith('<div class="alert-message alert-info">' + d + "</div>"));
    b.match("{alertwarning}") && (d = c.replace("{alertWarning}", ""), a.replaceWith('<div class="alert-message alert-warning">' + d + "</div>"));
    b.match("{alerterror}") &&
        (d = c.replace("{alertError}", ""), a.replaceWith('<div class="alert-message alert-error">' + d + "</div>"));
    b.match("{codebox}") && (b = c.replace("{codeBox}", ""), a.replaceWith('<pre class="code-box">' + b + "</pre>"))
});
