$(function() {
    function a(b) {
        $(b).each(function() {
            var c = $(this);
            if (1 == fixedMenu && 0 < c.length) {
                var d = $(document).scrollTop(),
                    e = c.offset().top,
                    f = c.height(),
                    g = e + f + f;
                $(window).scroll(function() {
                    var h = $(document).scrollTop(),
                        k = $("#footer-wrapper").offset().top,
                        l = 0 >= h;
                    ".main-nav-content" == b && (l = h < e);
                    h < k - f && (h > g ? c.addClass("is-fixed") : l && c.removeClass("is-fixed"), h > d ? c.removeClass("show") : c.addClass("show"), d = $(document).scrollTop())
                })
            }
        })
    }
    $(".index-post .entry-image-wrap .entry-thumb, .PopularPosts .entry-image-wrap .entry-thumb, .FeaturedPost .entry-image-wrap .entry-thumb,.entry-author .author-avatar,.about-author .author-avatar").lazyload();
    $("#mobile-menu").each(function() {
        var b = $(this),
            c = $("#main-nav-menu").clone();
        c.attr("id", "main-mobile-nav");
        c.find("li.is-icon > a").each(function() {
            var d = $(this);
            d.text(d.attr("title").trim())
        });
        c.find(".mega-items").remove();
        c.find(".mega-menu > a").each(function(d, e) {
            var f = $(this);
            e = "recent" == (d = shortCodePre(f.attr("shortcode").trim(), "label")) ? "/search" : "/search/label/" + d;
            f.attr("href", e)
        });
        c.appendTo(b);
        $(".mobile-menu-toggle, .hide-mobile-menu, .mobile-search-toggle, .overlay").on("click", function() {
            $("body").toggleClass("nav-active")
        });
        $(".mobile-menu .has-sub").append('<div class="submenu-toggle"/>');
        $(".mobile-menu .mega-menu").find(".submenu-toggle").remove();
        $(".mobile-menu ul li .submenu-toggle").on("click", function(d) {
            $(this).parent().hasClass("has-sub") && (d.preventDefault(), $(this).parent().hasClass("show") ? $(this).parent().removeClass("show").find("> .m-sub").slideToggle(170) : $(this).parent().addClass("show").children(".m-sub").slideToggle(170))
        })
    });
    $(".mobile-navbar-social").each(function() {
        var b = $(this);
        $("#header-icons ul.social").clone().appendTo(b)
    });
    $(".mobile-navbar-menu").each(function() {
        var b = $(this);
        $("#footer-menu ul.link-list").clone().appendTo(b)
    });
    a(".main-nav-content");
    a(".header-content");
    $("#main-wrapper, #sidebar-wrapper").each(function() {
        1 == fixedSidebar && $(this).theiaStickySidebarIfy({
            containerSelector: "#content-wrapper > .container",
            additionalMarginTop: 30,
            additionalMarginBottom: 30
        })
    });
    $("#post-body iframe").each(function() {
        var b = $(this);
        b.attr("src").match("www.youtube.com") && b.wrap('<div class="responsive-video-wrap"/>')
    });
    $("p.comment-content").each(function() {
        var b =
            $(this);
        b.replaceText(/(https:\/\/\S+(\.png|\.jpeg|\.jpg|\.gif))/g, '<img src="$1"/>');
        b.replaceText(/(?:https:\/\/)?(?:www\.)?(?:youtube\.com)\/(?:watch\?v=)?(.+)/g, '<div class="responsive-video-wrap"><iframe id="youtube" width="100%" height="358" src="https://www.youtube.com/embed/$1" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe></div>')
    });
    $("#loadmore").each(function() {
        var b = $(this).data("load");
        b &&
            $("#loadmore").show();
        $("#loadmore").on("click", function(c) {
            $("#loadmore").hide();
            $.ajax({
                url: b,
                success: function(d) {
                    var e = $(d).find(".blog-posts");
                    e.find(".index-post").addClass("post-animated post-fadeInUp");
                    $(".blog-posts").append(e.html());
                    (b = $(d).find("#loadmore").data("load")) ? $("#loadmore").show(): ($("#loadmore").hide(), $("#blog-pager .no-more").addClass("show"))
                },
                beforeSend: function() {
                    $("#blog-pager .loading").show()
                },
                complete: function() {
                    $("#blog-pager .loading").hide();
                    $("#main-wrapper, #sidebar-wrapper").each(function() {
                        1 == fixedSidebar && $(this).theiaStickySidebarIfy({
                            containerSelector: "#content-wrapper > .container",
                            additionalMarginTop: 30,
                            additionalMarginBottom: 30
                        })
                    });
                    $(".index-post .entry-image-wrap .entry-thumb").lazyload()
                }
            });
            c.preventDefault()
        })
    });
    $("#cookie-blogger").each(function() {
        var b = $(this);
        0 < b.length && ("1" !== $.cookie("cookie_consent") && (b.css("display",
            "block"), setTimeout(function() {
            b.addClass("is-visible")
        }, 10)), $("#cookie-blogger-accept").off("click").on("click", function(c) {
            c.preventDefault();
            c.stopPropagation();
            $.cookie("cookie_consent", "1", {
                expires: cookieExpiresDays,
                path: "/"
            });
            b.removeClass("is-visible");
            setTimeout(function() {
                b.css("display", "none")
            }, 500)
        }))
    });
    $("#back-top").each(function() {
        var b = $(this);
        $(window).on("scroll", function() {
            100 <= $(this).scrollTop() ? b.fadeIn(170) : b.fadeOut(170);
            b.offset().top >= $("#footer-wrapper").offset().top - 34 ?
                b.addClass("on-footer") : b.removeClass("on-footer")
        });
        b.on("click", function() {
            $("html, body").animate({
                scrollTop: 0
            }, 500)
        })
    })
});
